import React, { useRef, useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
import Nabvbar from "../Components/Nabvbar";
import { FaPlay, FaPause ,FaBars } from "react-icons/fa";
import Sidebar from "../Components/Sidebar";

export default function Play() {


   const songs = [
    { id: 1, name: "Coolie - Power House", desc: "“Unstoppable rhythmic power”", image: "data:image/jpeg;base64,", url : "/Songs/Powerhouse.mp3" ,titledes : "Artist: Aniruth Ravichandran"},
    { id: 2, name: "Dude - Oorum Blood", desc: "“Aggressive pulse surge”", image: "data:image/jpeg;base64,"  ,url: "/Songs/OorumBlood.mp3" ,titledes : "Artist: Sai Abiyankar"  },
    { id: 3, name: "Coolie - Monica", desc: "Energetic Bollywood dance track.", image: "data:image/jpeg;base64," , url:"/Songs/Monica.mp3"  , titledes : "Artist : Aniruth" },
    { id: 4, name: "Jana Nayagan - Thalapathy Kachery", desc: "High-energy celebratory Tamil mass anthem.", image: "https://i.ytimg.com/vi/icg4jqmxkwc/hq720.jpg?sqp=-oaymwEhCK4FEIIDSFryq4qpAxMIARUAAAAAGAElAADIQj0AgKJD&rs=AOn4CLB7SML6NO2LT5-9Md7XHRhFBT8vBQ" , url:"/Songs/ThalapathyKacheri.mp3" },
    { id: 5, name: "Baison - Theekoluthi", desc: "Raw, fiery, fast-paced street banger.", image: "data:image/jpeg;base64," , url:"/Songs/Theekkoluthi.mp3"},
    { id: 6, name: "Retro - The One", desc: "Emotional, melodic, soothing romantic track", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSyw06h9H-JTR_z5xU_QP42wf5bCtNK8631fA&s" , url:"/Songs/TheOne.mp3"},
    { id: 7, name: "Sithira Puthiri", desc: "Dreamy, playful, lighthearted romantic vibe", image: "data:image/jpeg;base64," , url:"/Songs/SithiraPuthiri.dev.mp3"},
    { id: 8, name: "Thalaivan Thalaivi - Pottala Muttayae", desc: "Energetic, cheeky, fast-paced folk banger.", image: "data:image/jpeg;base64," , url:"/Songs/PottalaMuttaye.mp3"},
    { id: 9, name: "Sita Raman - Kurumugil", desc: "Soft, emotional, soul-soothing romantic melody.", image: "data:image/jpeg;base64,", url:"/Songs/Kurumugil.dev.mp3" },
    { id: 10, name: "Amaran - Hey Minnalae", desc: "Bright, catchy, youthful romantic melody.", image: "data:image/jpeg;base64," , url:"/Songs/HeyMinnale.mp3"},
   
  ];


  const {id} = useParams()
const [setid , tosetId] = useState(id-1)

  // console.log(id)
  const location = useLocation();
  const song = location.state;
  const canvasRef = useRef();
  const audioRef = useRef();
  const [sidebarOpen, setSidebarOpen] = useState(false);
const [currentTime, setCurrentTime] = useState(0);


  const audioCtxRef = useRef(null);
  const analyserRef = useRef(null);
  const sourceRef = useRef(null);
  const rotationRef = useRef(0);

  const [playing, setPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [speed, setSpeed] = useState(1);

  const togglePlay = () => {
    if (!audioRef.current) return;
    if (playing) audioRef.current.pause();
    else audioRef.current.play();
    setPlaying(!playing);
  };

  const handleTimeUpdate = () => {
    if (!audioRef.current) return;
    setProgress(audioRef.current.currentTime);
  };

  const handleSeek = (e) => {
    if (!audioRef.current) return;
    audioRef.current.currentTime = e.target.value;
    setProgress(e.target.value);
  };

  const handleSpeedChange = (e) => {
    const newSpeed = parseFloat(e.target.value);
    if (!audioRef.current) return;
    audioRef.current.playbackRate = newSpeed;
    setSpeed(newSpeed);
  };

  useEffect(() => {
    
    if (!song || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const width = canvas.width = canvas.offsetWidth;
    const height = canvas.height = canvas.offsetHeight;
    

    const audio = audioRef.current;

    if (!audioCtxRef.current) {
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      audioCtxRef.current = audioCtx;

      const source = audioCtx.createMediaElementSource(audio);
      sourceRef.current = source;

      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      analyserRef.current = analyser;

      source.connect(analyser);
      analyser.connect(audioCtx.destination);
    }

    const analyser = analyserRef.current;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const img = new Image();
    img.src = song.image;

    img.onload = () => {
      function draw() {
        if (!ctx) return;
        requestAnimationFrame(draw);
        analyser.getByteFrequencyData(dataArray);

        ctx.clearRect(0, 0, width, height);

        const centerX = width / 2;
        const centerY = height / 2;
        const imgRadius = 80;

        // Draw album image
        ctx.save();
        ctx.beginPath();
        ctx.arc(centerX, centerY, imgRadius, 0, Math.PI * 2);
        ctx.closePath();
        ctx.clip();
        ctx.drawImage(img, centerX - imgRadius, centerY - imgRadius, imgRadius * 2, imgRadius * 2);
        ctx.restore();

        // Radial waveform
        const bars = 180;
        const angleOffset = rotationRef.current;

        for (let i = 0; i < bars; i++) {
          const angle = (i / bars) * Math.PI * 2 + angleOffset;
          const dataIndex = (i / bars) * dataArray.length;
          const indexLow = Math.floor(dataIndex);
          const indexHigh = Math.min(indexLow + 1, dataArray.length - 1);
          const value = dataArray[indexLow] * (indexHigh - dataIndex) + dataArray[indexHigh] * (dataIndex - indexLow);

          const barLength = value / 2;
          const r = 200;
          const g = 100 + value / 2;
          const b = 255 - value / 2;

          ctx.strokeStyle = `rgba(${r},${g},${b},0.9)`;
          ctx.lineWidth = 2;
          ctx.shadowBlur = 8;
          ctx.shadowColor = `rgba(${r},${g},${b},0.7)`;

          const startX = centerX + Math.cos(angle) * imgRadius;
          const startY = centerY + Math.sin(angle) * imgRadius;
          const endX = centerX + Math.cos(angle) * (imgRadius + barLength);
          const endY = centerY + Math.sin(angle) * (imgRadius + barLength);

          ctx.beginPath();
          ctx.moveTo(startX, startY);
          ctx.lineTo(endX, endY);
          ctx.stroke();
        }

        rotationRef.current += 0.002;
      }

      draw();
    };

    // Set duration after metadata loads
    audio.onloadedmetadata = () => {
      setDuration(audio.duration);
    };
    audio.ontimeupdate = handleTimeUpdate;
  audio.onended = () => {setPlaying(false), handleNext()};

  }, [playing]);

  if (!song) return <p className="text-white">No song selected!</p>;

  useEffect(()=>{
    setPlaying(true)
      const audio = audioRef.current;
      audio.onended = () => {setPlaying(false), handleNext()};
  },[setid])

  function handleNext()
  {
    if(setid <9)
    {
      tosetId(setid+1)
    }
    else{ tosetId(0) 
    
    }
  }

  function handleprev()
  {
    if(setid >0)
    {
      tosetId(setid-1)
    }
    else{
      tosetId(9)
    }
  }


  return (
    <>
      <Nabvbar />
     <div className="flex">
  {/* Sidebar */}
 <Sidebar isOpen={sidebarOpen} onClose={() => setSidebarOpen(false)} />
  


  {/* Main content */}
  <div className="flex-1 relative bg-gray-900 min-h-screen">
    {/* Hamburger menu on mobile */}
<button
  className="sm:hidden absolute top-4 left-4 text-white text-2xl z-50"
  onClick={() => setSidebarOpen(true)}
>
  <FaBars />
</button>




    {/* Player container */}
    <div className="absolute top-18 right-0 w-full sm:w-[calc(100%-18rem)] p-4 flex flex-col items-center pt-30">
      <div className="relative w-full max-w-md sm:max-w-lg bg-gray-800 rounded-3xl shadow-xl flex flex-col items-center justify-center p-4 h-50">
        {/* <canvas ref={canvasRef} className="absolute inset-0  rounded-3xl " /> */}
        <div className="absolute flex flex-col items-center justify-center gap-4 z-10">
          <div className="w-40 h-40 rounded-full overflow-hidden shadow-2xl">
            <img src={songs[setid].image} alt={songs[setid].name} className="w-full h-full object-cover" />
          </div>
          <button
            onClick={togglePlay}
            className="bg-red-500 hover:bg-purple-600 p-4 rounded-full shadow-lg text-white text-2xl flex items-center justify-center"
          >
            {playing ? <FaPause /> : <FaPlay />}
          </button>
        </div>
        <audio ref={audioRef} src={songs[setid].url} autoPlay   onTimeUpdate={() => { setCurrentTime(audioRef.current.currentTime) } }/>
      </div>
<div className=" pt-10">
      <h2 className="text-white mt-6 text-2xl font-semibold text-center">{songs[setid].name}</h2>
      <p className="text-gray-400 mt-1 text-center">{songs[setid].desc}</p>
</div>
      {/* Controls */}
      <div className="flex flex-col items-center gap-4 mt-6 w-full max-w-md">
    
     <input
  type="range"
  min="0"
  max={audioRef.current?.duration || 0}
  value={currentTime}
  onChange={(e) => {
    const newTime = Number(e.target.value);
    audioRef.current.currentTime = newTime;
    setCurrentTime(newTime);
  }}
   className="w-full h-1 accent-red-500 rounded-lg cursor-pointer"
/>
<button onClick={handleNext}>Next</button> <button onClick={handleprev}>Previous</button>
        <div className="flex items-center justify-between w-full">
          <a href={songs[setid].url} download className="bg-gray-500 text-white px-4 py-2 rounded">
            Download
          </a>
          <div className="flex items-center gap-2">
            <p className="text-white">Speed:</p>
            <select
              value={speed}
              onChange={handleSpeedChange}
              className="bg-gray-800 text-white px-2 py-1 rounded"
            >
              <option value="0.5">0.5x</option>
              <option value="1">1x</option>
              <option value="1.5">1.5x</option>
              <option value="2">2x</option>
            </select>
          </div>
        </div>
      </div>
    </div>
  </div>
  <button
  className="sm:hidden absolute top-4 left-4 text-white text-2xl z-50"
  onClick={() => setSidebarOpen(true)}
>
  <FaBars />
</button>

</div>

    </>
  );
}
